package p01;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
    for(int i=1;i<=10; i++) {
    	if(i %2 ==1) {
    	System.out.println(i);
      }
	}

    for(int i=2;i<=10; i+=2) {
    {
    	System.out.println(i);
      }
	}
	}
}
