public class Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int i =1;
        System.out.println(++i);
        System.out.println(i++);
        System.out.println(i);
        
        int nume =5;
        System.out.println(nume/2);//묷
        System.out.println(nume % 2);//나머지
        
	}

}
